// Build Google Drive links from json.id
// Input: items with item.json.id (from Google Drive node output)
// Output: adds driveFileUrl, webViewLink, downloadLink to item.json

const items = $input.all().map((item) => {
  item.json = item.json || {};

  // Try a few common id fields just in case
  const id =
    item.json.id ||
    item.json.fileId ||
    item.json.file_id ||
    $json.id;

  if (!id) {
    // Surface a helpful error that includes the item index
    throw new Error(`Missing file id on item: ${JSON.stringify(item.json)}`);
  }

  const base = 'https://drive.google.com/file/d/';
  const safeId = encodeURIComponent(id);

  // Pretty file URL (opens Drive file page)
  item.json.driveFileUrl = `${base}${safeId}`;

  // Typical "Open in Drive" view link
  item.json.webViewLink = `${base}${safeId}/view?usp=sharing`;

  // Direct-ish download link pattern used by Google
  item.json.downloadLink = `https://drive.google.com/uc?id=${safeId}&export=download`;

  return item;
});

return items;

// Combine HTML from "Landing Page Generator" and emit as binary `data`

// 1) Read the HTML produced by the Landing Page Generator
let html = $node["Landing Page Generator"].json?.text || $json?.text || "";

if (!html || typeof html !== "string" || html.trim().length < 100) {
  throw new Error("Landing Page HTML is missing or too short. Check the 'Landing Page Generator' node output.");
}

// 2) Strip any accidental Markdown/code fences (handles ``` and ```html anywhere)
const stripCodeFences = (s) => {
  // Remove leading/trailing fences
  // Matches ``` or ```html (case-insensitive), possibly with whitespace
  const fenceStart = /^\s*```(?:html)?\s*/i;
  const fenceEnd = /\s*```\s*$/i;
  s = s.replace(fenceStart, "");
  s = s.replace(fenceEnd, "");

  // If the whole blob contains multiple fenced blocks, try to extract the first fenced block contents
  const multiFence = /```(?:html)?\s*([\s\S]*?)\s*```/i;
  const m = s.match(multiFence);
  if (m && m[1]) return m[1];

  return s;
};

html = stripCodeFences(html).trim();

// 3) Normalizers
const hasDoctype = /<!DOCTYPE\s+html>/i.test(html);
const hasHtmlTag = /<html[\s>]/i.test(html);
const endsWithHtmlClose = /<\/html>\s*$/i.test(html);

// 4) If there's no <html> tag, treat it as a fragment and wrap it fully
const wrapFragment = (fragment) => `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Landing Page</title>
</head>
<body>
${fragment}
</body>
</html>`;

// 5) Ensure full document
if (!hasHtmlTag) {
  html = wrapFragment(html);
} else {
  // Ensure DOCTYPE
  if (!hasDoctype) {
    html = `<!DOCTYPE html>\n${html}`;
  }
  // If somehow missing closing </html>, append it
  if (!endsWithHtmlClose) {
    // Try to close body if present but not closed (best-effort)
    const hasBodyOpen = /<body[\s>]/i.test(html);
    const hasBodyClose = /<\/body>/i.test(html);
    if (hasBodyOpen && !hasBodyClose) {
      html = `${html}\n</body>`;
    }
    html = `${html}\n</html>`;
  }
}

// 6) Final sanity (no throwing: we coerce instead)
if (!/^<!DOCTYPE html>/i.test(html)) {
  html = `<!DOCTYPE html>\n${html.replace(/^\uFEFF/, "")}`; // strip BOM if present, then add doctype
}
if (!/<html[\s>]/i.test(html)) {
  // As an absolute fallback, wrap again
  html = wrapFragment(html);
}
if (!/<\/html>\s*$/i.test(html)) {
  html = `${html}\n</html>`;
}

// 7) Build filename like Client_YYYY-MM-DD_HH-mm.html
const pad = (n) => String(n).padStart(2, "0");
const now = new Date();
const fileName = `Client_${now.getFullYear()}-${pad(now.getMonth() + 1)}-${pad(now.getDate())}_${pad(now.getHours())}-${pad(now.getMinutes())}.html`;

// 8) Prepare output structure (json metadata + binary `data`)
const buffer = Buffer.from(html, "utf8");
const binaryData = await this.helpers.prepareBinaryData(buffer, fileName, "text/html");

return [
  {
    json: {
      fileName,
      size: buffer.length,
      validated: true,
      timestamp: now.toISOString(),
    },
    binary: {
      data: binaryData, // <-- The next Google Drive node should use "Binary Property: data"
    },
  },
];

// Consolidate mixed items (scrape results, screenshots, errors) into ONE item
// and expose clean fields for the LLM prompt:
//
//  Output JSON shape:
//  {
//    client: {
//      url, html, markdown, text, metadata, screenshotUrl
//    },
//    competitor: {
//      url, screenshotUrl
//    },
//    raw: { allItems, errors }   // optional debug
//  }

const items = $input.all();

// Helper: shallow clone (avoid mutating upstream)
const clone = (obj) => JSON.parse(JSON.stringify(obj || {}));

// Buckets
let client = { url: null, html: null, markdown: null, text: null, metadata: null, screenshotUrl: null };
let competitor = { url: null, screenshotUrl: null };
const errors = [];

// Utilities
const getHost = (u) => {
  try { return new URL(u).host; } catch { return null; }
};
const looksLikeScrape = (j) =>
  j && (j.html || j.markdown || j.text || j.metadata);
const looksLikeScreenshot = (j) =>
  j && (j.screenshotUrl && (j.url || j.startUrl));

// First, gather all candidates
const scrapeCandidates = [];
const screenshotCandidates = [];

for (const it of items) {
  const j = it.json || {};

  if (j.error) {
    errors.push(clone(j));
    continue;
  }

  if (looksLikeScrape(j)) {
    scrapeCandidates.push(clone(j));
  } else if (looksLikeScreenshot(j)) {
    screenshotCandidates.push(clone(j));
  }
}

// Heuristics to pick client vs competitor
// If two distinct hosts exist in screenshots, treat the first host we see as "client" and the second as "competitor".
// (Works with your example: butterflydigital.ca = client, newdgtl.com = competitor)
const uniqBy = (arr, keyFn) => {
  const seen = new Set();
  return arr.filter(x => {
    const k = keyFn(x);
    if (k && !seen.has(k)) { seen.add(k); return true; }
    return false;
  });
};

const ssWithHost = screenshotCandidates
  .map(s => ({...s, host: getHost(s.url || s.startUrl)}))
  .filter(s => !!s.host);

const dedupScreens = uniqBy(ssWithHost, s => s.screenshotUrl || (s.url || s.startUrl));

let clientHost = null;
let competitorHost = null;

if (dedupScreens.length) {
  clientHost = dedupScreens[0]?.host || null;
  // find a different host to mark as competitor
  const other = dedupScreens.find(s => s.host !== clientHost);
  competitorHost = other?.host || null;
}

// Assign screenshot URLs
for (const s of dedupScreens) {
  const url = s.url || s.startUrl || null;

  if (clientHost && s.host === clientHost && !client.screenshotUrl) {
    client.screenshotUrl = s.screenshotUrl;
    client.url = url;
  } else if (competitorHost && s.host === competitorHost && !competitor.screenshotUrl) {
    competitor.screenshotUrl = s.screenshotUrl;
    competitor.url = url;
  }
}

// Pick best client scrape content matching client host if possible, else first scrape
const scrapeForHost = (host) =>
  scrapeCandidates.find(c => getHost(c.url || c.crawl?.loadedUrl || c.metadata?.canonicalUrl) === host);

let clientScrape =
  (clientHost && scrapeForHost(clientHost)) ||
  scrapeCandidates[0] ||
  null;

if (clientScrape) {
  client.html = clientScrape.html || null;
  client.markdown = clientScrape.markdown || null;
  client.text = clientScrape.text || null;
  client.metadata = clientScrape.metadata || null;
  client.url = client.url || clientScrape.url || clientScrape.crawl?.loadedUrl || clientScrape.metadata?.canonicalUrl || null;
}

// If we still don't have competitor screenshot but we have another screenshot host, use it
if (!competitor.screenshotUrl && dedupScreens.length > 1) {
  const candidate = dedupScreens.find(s => s.host !== clientHost);
  if (candidate) {
    competitor.screenshotUrl = candidate.screenshotUrl;
    competitor.url = candidate.url || candidate.startUrl || null;
  }
}

// Final single consolidated item
return [{
  json: {
    client,
    competitor,
    raw: {
      allItems: items.map(i => i.json || {}),
      errors
    }
  }
}];
