parameters.jsCode:
// Extract client and project details from Notion database pages
const projects = [];
const items = $input.all();

// Process each database page
for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Extract page ID and remove dashes to match content data format
  const pageIdWithDashes = item.json.id || 'unknown';
  const pageId = pageIdWithDashes.replace(/-/g, ''); // Remove all dashes

  // Extract client name - Check multiple possible property names
  const client = item.json.property_client || 
    item.json.properties?.Client?.select?.name || 
    item.json.properties?.Client?.rich_text?.[0]?.text?.content ||
    item.json.properties?.Client?.title?.[0]?.text?.content ||
    'Unknown Client';

  // Extract project/activity name
  const projectName = item.json.name ||
    item.json.property_project_name ||
    item.json.properties?.Name?.title?.[0]?.text?.content ||
    item.json.properties?.Title?.title?.[0]?.text?.content ||
    item.json.properties?.Activity?.title?.[0]?.text?.content ||
    item.json.properties?.Project?.title?.[0]?.text?.content ||
    'Untitled Project';

  // Extract owner(s)
  const owners = item.json.property_owner || 
    item.json.properties?.Owner?.people?.map(p => p.name || p.email) || 
    [];

  // Extract status
  const status = item.json.property_status ||
    item.json.properties?.Status?.status?.name ||
    item.json.properties?.Status?.select?.name ||
    'Active';

  // Extract dates
  const startDate = item.json.property_start_date?.start || 
    item.json.properties?.StartDate?.date?.start || '';
  const endDate = item.json.property_end_date?.start || 
    item.json.properties?.EndDate?.date?.start || '';
  const lastEdited = item.json.property_last_edited_time || 
    item.json.last_edited_time || '';

  // Extract priority
  const priority = item.json.property_priority ||
    item.json.properties?.Priority?.select?.name ||
    item.json.properties?.Priority?.multi_select?.[0]?.name ||
    'Normal';

  // Extract service type
  const serviceType = item.json.property_service_type || 
    item.json.properties?.ServiceType?.select?.name || '';

  // Extract project links
  const projectLinks = item.json.property_project_links || 
    item.json.properties?.ProjectLinks?.url || [];

  // Add to projects array
  projects.push({
    pageId: pageId,
    pageIdOriginal: pageIdWithDashes,
    client: client,
    projectName: projectName,
    url: item.json.url || '',
    owners: owners,
    ownersList: Array.isArray(owners) ? owners.join(', ') : owners,
    status: status,
    startDate: startDate,
    endDate: endDate,
    lastEditedTime: lastEdited,
    priority: priority,
    serviceType: serviceType,
    projectLinks: Array.isArray(projectLinks) ? projectLinks : [projectLinks].filter(l => l),
    hasLinks: projectLinks && (Array.isArray(projectLinks) ? projectLinks.length > 0 : true)
  });
}

// Return the extracted project information
return [{
  json: {
    projects: projects,
    totalProjects: projects.length,
    uniqueClients: [...new Set(projects.map(p => p.client))]
  }
}];

// Aggregate all items into a single data structure for AI processing
const allItems = $input.all();

// Separate blocks and project data
const blocks = [];
let projectData = null;

// Process all items and identify their types
for (const item of allItems) {
  if (item.json.projects) {
    // This is the project metadata
    projectData = item.json;
  } else if (item.json.type || item.json.object === 'block') {
    // This is a Notion block
    blocks.push(item.json);
  }
}

// Create aggregated data structure
const aggregatedData = {
  metadata: {
    totalItems: allItems.length,
    blockCount: blocks.length,
    timestamp: new Date().toISOString()
  },
  projects: projectData ? projectData.projects : [],
  blocks: blocks,
  uniqueClients: projectData ? projectData.uniqueClients : []
};

// Return as single item for AI processing
return [{
  json: aggregatedData
}];

