// Format the AI-generated JSON into an email
const item = $input.all()[0];
let reportData;

// The output parser ensures we get properly structured JSON
if (item.json.output) {
  reportData = item.json.output;
} else if (item.json.clients) {
  reportData = item.json;
} else {
  // Fallback
  reportData = {
    reportDate: new Date().toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    }),
    clients: [],
    statistics: { totalClients: 0, totalProjects: 0, projectsWithUpdates: 0 }
  };
}

const currentDate = reportData.reportDate || new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Create HTML email content
let htmlContent = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>Weekly Client Activity Report</title>
  <style>
    /* Base / tokens */
    :root { color-scheme: light dark; }
    body { 
      margin: 0; 
      padding: 0; 
      background: #f5f7fb; 
      -webkit-text-size-adjust: 100%;
      -ms-text-size-adjust: 100%;
    }
    .email-container {
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Helvetica Neue', Arial, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', sans-serif;
      line-height: 1.55;
      color: #2b2f38;
      max-width: 860px;
      margin: 0 auto;
      padding: 16px;
    }
    .card {
      background: #ffffff;
      border-radius: 14px;
      padding: 24px;
      box-shadow: 0 1px 3px rgba(16,24,40,0.06), 0 1px 2px rgba(16,24,40,0.04);
      border: 1px solid #eef2f7;
    }

    /* Headings */
    h1 { 
      margin: 0 0 12px 0;
      font-size: 24px;
      line-height: 1.25;
      letter-spacing: -0.02em;
      color: #1f2a37;
    }
    .subtle {
      color: #667085;
      font-size: 14px;
      margin: 0 0 20px 0;
    }

    /* Section titles */
    h2 { 
      color: #1f2a37; 
      margin: 28px 0 12px; 
      font-size: 18px;
      padding: 10px 12px;
      background: #f3f7ff;
      border: 1px solid #e4edff;
      border-left: 4px solid #3b82f6;
      border-radius: 10px;
    }
    h3 {
      color: #344054;
      margin: 10px 0 8px;
      font-size: 16px;
    }

    /* Meta rows */
    .row, .client-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 12px;
      flex-wrap: wrap;
      margin-bottom: 10px;
    }

    /* Stats ‚Üí single row on desktop, stack on phones */
    .stats {
      background: #f3f7ff;
      border: 1px solid #e4edff;
      padding: 0;
      border-radius: 14px;
      margin: 18px 0 8px 0;
    }
    .stats-strip {
      display: flex;
      align-items: stretch;
      justify-content: space-between;
      gap: 0;
      overflow: hidden;
      border-radius: 12px;
      background: #f8fbff;
    }
    .stat-cell {
      flex: 1 1 0%;
      display: grid;
      place-items: center;
      padding: 18px 12px;
      text-align: center;
      background: #ffffff;
    }
    .stat-cell + .stat-cell { border-left: 1px solid #e8efff; }
    .stat-value {
      font-size: 22px;
      font-weight: 800;
      color: #3b82f6;
      line-height: 1;
      margin-bottom: 4px;
    }
    .stat-label { color: #667085; font-size: 12.5px; }

    /* Pills / badges */
    .pill, .project-status {
      display: inline-flex;
      align-items: center;
      gap: 8px;
      padding: 6px 12px;
      border-radius: 999px;
      font-size: 12px;
      font-weight: 700;
      line-height: 1;
      white-space: nowrap;
      border: 1px solid transparent;
      box-shadow: 0 1px 0 rgba(16,24,40,0.06);
    }
    .project-status .dot {
      width: 8px;
      height: 8px;
      border-radius: 999px;
      background: currentColor;
      display: inline-block;
    }

    /* Status palettes */
    .status-in-progress {
      color: #0b4a6f;
      background: linear-gradient(180deg, #eaf6ff 0%, #e3f1ff 100%);
      border-color: #cfe9ff;
    }
    .status-active, .status-done {
      color: #146c43;
      background: linear-gradient(180deg, #eafaf0 0%, #e3f7ea 100%);
      border-color: #ccebd7;
    }
    .status-needs-review, .status-review {
      color: #8a6100;
      background: linear-gradient(180deg, #fff7e6 0%, #fff1d6 100%);
      border-color: #ffe7b7;
    }
    .status-completed {
      color: #193b7b;
      background: linear-gradient(180deg, #edf3ff 0%, #e8efff 100%);
      border-color: #d8e3ff;
    }
    .status-to-do {
      color: #8a1c1c;
      background: linear-gradient(180deg, #ffecec 0%, #ffe3e3 100%);
      border-color: #ffd3d3;
    }

    /* Priority colors (text only) */
    .priority-high, .priority-very-high { color: #d92d20; font-weight: 700; }
    .priority-medium { color: #b54708; font-weight: 600; }
    .priority-low { color: #067647; font-weight: 600; }
    .priority-normal { color: #475467; }

    .project-count {
      background: #3b82f6;
      color: white;
      padding: 6px 12px;
      border-radius: 999px;
      font-size: 12px;
      font-weight: 700;
    }

    /* Cards & sections */
    .summary-section { 
      background: #f8fafc; 
      padding: 16px; 
      margin: 12px 0; 
      border-radius: 12px; 
      border: 1px solid #eef2f7;
    }
    .project-card {
      background: #ffffff;
      padding: 16px;
      margin: 14px 0;
      border-radius: 12px;
      border: 1px solid #eef2f7;
    }
    .project-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 12px;
      flex-wrap: wrap;
      margin-bottom: 8px;
    }
    .project-title {
      margin: 0;
      font-size: 16px;
      font-weight: 700;
      color: #1f2a37;
    }
    .project-meta { color: #667085; font-size: 12.5px; }

    /* Lists */
    ul { margin: 8px 0 0 0; padding-left: 18px; }
    li { margin: 6px 0; line-height: 1.45; }
    strong { color: #1f2a37; font-weight: 650; }

    .action-items {
      background: #fff7e6;
      border: 1px solid #ffe7b7;
      padding: 12px;
      border-radius: 10px;
      margin: 10px 0;
    }
    .next-steps {
      background: #eaf6ff;
      border: 1px solid #cfe9ff;
      padding: 12px;
      border-radius: 10px;
      margin: 10px 0;
    }

    .footer { 
      margin-top: 28px; 
      padding-top: 16px; 
      border-top: 1px solid #eef2f7; 
      color: #667085; 
      font-size: 12.5px;
      text-align: center;
    }

/* Space and align pills consistently */
.project-count,
.project-status,
.pill {
  margin-left: 6px;        /* space from label text */
  vertical-align: middle;  /* align with text baseline */
}

.client-header .project-count {
  margin-left: 8px;        /* a bit more space for the client header count */
}

.project-status,
.pill {
  display: inline-flex;    /* keeps dot and text aligned */
  align-items: center;
  gap: 6px;                /* space between dot and text */
  margin-top: 2px;         /* tweak vertical position */
  margin-bottom: 2px;
}


    a { color: #3b82f6; text-decoration: none; }
    a:hover { text-decoration: underline; }

    /* Responsive */
    @media only screen and (max-width: 560px) {
      .email-container { padding: 12px; }
      .card { padding: 18px; border-radius: 12px; }
      h1 { font-size: 20px; }
      h2 { font-size: 16px; padding: 8px 10px; }
      h3 { font-size: 15px; }
      .stats-strip { flex-direction: column; }
      .stat-cell + .stat-cell { border-left: 0; border-top: 1px solid #e8efff; }
      .project-title { font-size: 15px; }
      .row, .project-header, .client-header { gap: 8px; }
      .project-status, .pill { font-size: 11.5px; padding: 5px 10px; }
      .project-meta { font-size: 12px; }
      .project-count { padding: 5px 10px; font-size: 11.5px; }
    }

    /* Dark mode (basic) */
    @media (prefers-color-scheme: dark) {
      body { background: #0b1220; }
      .card, .project-card { background: #0f172a; border-color: #1e293b; }
      .email-container, .subtle, .project-meta, .stat-label { color: #cbd5e1; }
      h1, h2, h3, strong { color: #e2e8f0; }
      .summary-section { background: #0f172a; border-color: #1e293b; }
      .stats { background: #0f172a; border-color: #1e293b; }
      .stat-cell { background: #0f172a; }
      a { color: #93c5fd; }
    }
  </style>
</head>
<body>
  <div class="email-container">
    <div class="card">
      <h1>üìä Weekly Client Activity Report</h1>
      <p class="subtle"><strong>Report Date:</strong> ${currentDate}</p>

      <!-- Stats: single-row on desktop -->
      <div class="stats">
        <div class="stats-strip">
          <div class="stat-cell">
            <div class="stat-value">${reportData.statistics?.totalClients || 0}</div>
            <div class="stat-label">Active Clients</div>
          </div>
          <div class="stat-cell">
            <div class="stat-value">${reportData.statistics?.totalProjects || 0}</div>
            <div class="stat-label">Total Projects</div>
          </div>
          <div class="stat-cell">
            <div class="stat-value">${reportData.statistics?.projectsWithUpdates || 0}</div>
            <div class="stat-label">Projects with Updates</div>
          </div>
        </div>
      </div>
`;

// Add each client section
if (reportData.clients && reportData.clients.length > 0) {
  for (const client of reportData.clients) {
    htmlContent += `
    <h2>üè¢ ${client.name}</h2>
    <div class="client-header">
      <span><strong>Projects:</strong> ${client.projectCount || 0} total</span>
      <span class="project-count">${client.projects?.length || 0} Active</span>
    </div>
    `;

    // Client summary
    if (client.clientSummary) {
      htmlContent += `
      <div class="summary-section">
        <h3>Client Overview</h3>
        <p>${client.clientSummary}</p>
      </div>`;
    }

    // Key highlights
    if (client.keyHighlights && client.keyHighlights.length > 0) {
      htmlContent += `
      <div class="summary-section">
        <h3>Key Highlights</h3>
        <ul>${client.keyHighlights.map(h => `<li>${h}</li>`).join('')}</ul>
      </div>`;
    }

    // Projects
    if (client.projects && client.projects.length > 0) {
      for (const project of client.projects) {
        const status = (project.status || 'Active').toLowerCase().replace(/\s+/g, '-');
        const statusClass = `status-${status}`;
        const priority = (project.priority || 'Normal').toLowerCase().replace(/\s+/g, '-');
        const priorityClass = `priority-${priority}`;

        htmlContent += `
        <div class="project-card">
          <div class="project-header">
            <h3 class="project-title">${project.name}</h3>
            <span class="project-status ${statusClass}">
              <span class="dot"></span>${project.status || 'Active'}
            </span>
          </div>
        `;

        // Project metadata
        if (project.owner || project.priority || project.startDate || project.endDate) {
          htmlContent += `<p class="project-meta">`;
          if (project.owner) htmlContent += `<strong>Owner:</strong> ${project.owner} | `;
          if (project.priority) htmlContent += `<strong>Priority:</strong> <span class="${priorityClass}">${project.priority}</span> | `;
          if (project.startDate) htmlContent += `<strong>Start:</strong> ${project.startDate} | `;
          if (project.endDate) htmlContent += `<strong>End:</strong> ${project.endDate}`;
          htmlContent += `</p>`;
        }

        // Summary
        if (project.summary) {
          htmlContent += `
          <div style="margin: 10px 0;">
            <strong>Summary:</strong>
            <p>${project.summary}</p>
          </div>`;
        }

        // Action items
        if (project.actionItems && project.actionItems.length > 0) {
          htmlContent += `
          <div class="action-items">
            <strong>üìå Action Items:</strong>
            <ul>${project.actionItems.slice(0, 5).map(item => `<li>${item}</li>`).join('')}</ul>
          </div>`;
        }

        // Next steps
        if (project.nextSteps && project.nextSteps.length > 0) {
          htmlContent += `
          <div class="next-steps">
            <strong>‚û°Ô∏è Next Steps:</strong>
            <ul>${project.nextSteps.slice(0, 5).map(step => `<li>${step}</li>`).join('')}</ul>
          </div>`;
        }

        htmlContent += `</div>`; // close project-card
      }
    }
  }
} else {
  htmlContent += `
    <div class="summary-section">
      <p>No client data available for this period.</p>
    </div>`;
}

// Footer
htmlContent += `
    <div class="footer">
      <p><strong>üìß Automated Report</strong></p>
      <p>This report was automatically generated from your Notion database.</p>
      <p>For detailed information about any activity, please refer to your <a href="https://notion.so">Notion workspace</a>.</p>
    </div>
  </div>
</div>
</body>
</html>
`;

// Create plain text version
let textContent = `WEEKLY CLIENT ACTIVITY REPORT
Report Date: ${currentDate}

SUMMARY STATISTICS
==================
Active Clients: ${reportData.statistics?.totalClients || 0}
Total Projects: ${reportData.statistics?.totalProjects || 0}
Projects with Updates: ${reportData.statistics?.projectsWithUpdates || 0}

${'='.repeat(60)}

`;

if (reportData.clients && reportData.clients.length > 0) {
  for (const client of reportData.clients) {
    textContent += `CLIENT: ${client.name}
Projects: ${client.projectCount || 0} total

`;
    if (client.clientSummary) {
      textContent += `Overview: ${client.clientSummary}

`;
    }
    if (client.projects && client.projects.length > 0) {
      for (const project of client.projects) {
        textContent += `
PROJECT: ${project.name}
Status: ${project.status || 'Active'}
${project.owner ? `Owner: ${project.owner}\n` : ``}
${project.priority ? `Priority: ${project.priority}\n` : ``}
${project.summary ? `\nSummary:\n${project.summary}\n` : ``}
${project.actionItems && project.actionItems.length > 0 ? `\nAction Items:\n- ${project.actionItems.slice(0, 5).join('\n- ')}\n` : ``}
${project.nextSteps && project.nextSteps.length > 0 ? `\nNext Steps:\n- ${project.nextSteps.slice(0, 5).join('\n- ')}\n` : ``}
`;
      }
    }
    textContent += `${'-'.repeat(50)}\n\n`;
  }
}

textContent += `
========================================
This report was automatically generated from your Notion database.
For detailed information about any activity, please refer to your Notion workspace.`;

// Return formatted email content
return [{
  json: {
    subject: `üìä Weekly Client Activity Report - ${currentDate} | ${reportData.statistics?.totalClients || 0} Clients`,
    htmlContent,
    textContent,
    totalClients: reportData.statistics?.totalClients || 0,
    totalProjects: reportData.statistics?.totalProjects || 0,
    projectsWithUpdates: reportData.statistics?.projectsWithUpdates || 0,
    reportDate: currentDate
  }
}];
