// Format AI-generated summary into Slack message
const aiSummary = $input.all()[0].json.output || $input.all()[0].json;

// Set target channel based on client
const clientChannelMap = {
  'SimplifAI': 'simplifai-updates',
  'WSI': 'wsi-updates',
  'BFD': 'bfd-client-channel'
};
const targetChannel = clientChannelMap[aiSummary.clientName] || 'general-updates';

// Format status with emoji
const getStatusEmoji = (status) => {
  const statusLower = (status || '').toLowerCase();
  if (statusLower.includes('track')) return ':white_check_mark:';
  if (statusLower.includes('attention') || statusLower.includes('review')) return ':warning:';
  if (statusLower.includes('blocked') || statusLower.includes('critical')) return ':red_circle:';
  return ':large_blue_circle:';
};

// Create Slack message
const overallStatusEmoji = getStatusEmoji(aiSummary.overallStatus);
const currentDate = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

let slackMessage = `:chart_with_upwards_trend: *Dev Board | Daily Project Update for ${aiSummary.clientName}*\n`;
slackMessage += `:calendar: ${currentDate}\n\n`;

// Overall status in blockquote
slackMessage += `>${overallStatusEmoji} *Overall Status:* ${aiSummary.overallStatus}\n`;
slackMessage += `>:chart_with_upwards_trend: *Active Projects:* ${aiSummary.projectCount}\n`;
slackMessage += `>:memo: *Last Update:* ${new Date(aiSummary.lastUpdateDate).toLocaleDateString()}\n\n`;

// Project-specific updates
if (aiSummary.projectUpdates && aiSummary.projectUpdates.length > 0) {
  aiSummary.projectUpdates.forEach((project, index) => {
    const projectStatusEmoji = getStatusEmoji(project.status);

    // Project header in blockquote
    slackMessage += `>${projectStatusEmoji} *${project.projectName}*\n`;
    slackMessage += `>_Status: ${project.status}_\n\n`;

    // Latest updates for this project
    if (project.latestUpdates && project.latestUpdates.length > 0) {
      slackMessage += `*:memo: Recent Updates:*\n`;
      project.latestUpdates.forEach(update => {
        slackMessage += `â€¢ ${update}\n`;
      });
      slackMessage += `\n`;
    }

    // Next actions for this project
    if (project.nextActions && project.nextActions.length > 0) {
      slackMessage += `*:dart: Next Actions:*\n`;
      project.nextActions.forEach(action => {
        slackMessage += `â€¢ ${action}\n`;
      });
      slackMessage += `\n`;
    }

    // Blockers for this project
    if (project.blockers && project.blockers.length > 0) {
      slackMessage += `*:warning: Blockers:*\n`;
      project.blockers.forEach(blocker => {
        slackMessage += `â€¢ ${blocker}\n`;
      });
      slackMessage += `\n`;
    }

    // Add spacing between projects (except for last project)
    if (index < aiSummary.projectUpdates.length - 1) {
      slackMessage += `\nâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€\n\n`;
    }
  });
}

// Slack context in blockquote
if (aiSummary.slackContext && aiSummary.slackContext.trim() !== '') {
  slackMessage += `\n*:speech_balloon: Recent Team Discussions:*\n`;
  slackMessage += `>${aiSummary.slackContext}\n\n`;
}

// Footer
slackMessage += `â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€\n`;
slackMessage += `:robot_face: _Automated update from Notion projects_\n`;
slackMessage += `:clipboard: _For detailed project info, check your Notion workspace_`;

// Return formatted message
return [{
  json: {
    channelName: targetChannel,
    message: slackMessage,
    clientName: aiSummary.clientName,
    overallStatus: aiSummary.overallStatus,
    projectCount: aiSummary.projectCount
  }
}];

