// Combine project data with Slack history for AI processing
const allInputs = $input.all();
console.log('Total inputs received:', allInputs.length);

// Initialize data containers
let projects = [];
let notionBlocks = [];
let slackHistory = [];
let currentClient = 'SimplifAI';
let targetChannel = 'simplifai-updates';

// Process all merged inputs to separate different data types
for (let i = 0; i < allInputs.length; i++) {
  const input = allInputs[i].json;

  // Check if this is aggregated project data
  if (input.currentClient !== undefined && input.projects !== undefined) {
    // This is from Aggregate Client Data
    currentClient = input.currentClient;
    targetChannel = input.targetChannel;
    projects = input.projects || [];
    notionBlocks = [...notionBlocks, ...(input.blocks || [])];
    console.log('Found aggregated project data:', currentClient, 'projects:', projects.length);
  }
  // Check if this is a Slack message
  else if (input.ts && (input.type === 'message' || input.subtype)) {
    slackHistory.push(input);
  }
  // Check if this is a Notion block
  else if (input.object === 'block') {
    notionBlocks.push(input);
  }
  // Check if this item has project-like properties (fallback)
  else if (input.pageId || input.projectName || input.client) {
    projects.push(input);
    // Extract client info if not set
    if (currentClient === 'SimplifAI' && input.client) {
      currentClient = input.client;
    }
  }
}

console.log('Data separation results:', {
  projects: projects.length,
  notionBlocks: notionBlocks.length,
  slackMessages: slackHistory.length,
  currentClient: currentClient
});

// Extract client from projects if still unknown
if (currentClient === 'Unknown Client' && projects.length > 0) {
  currentClient = projects[0].client || 'SimplifAI';
  console.log('Extracted client from projects:', currentClient);
}

// Set target channel based on client
if (targetChannel === 'unknown-channel') {
  const clientChannelMap = {
    'SimplifAI': 'simplifai-updates',
    'WSI': 'wsi-updates',
    'BFD': 'bfd-client-channel'
  };
  targetChannel = clientChannelMap[currentClient] || `${currentClient.toLowerCase().replace(/\s+/g, '-')}-updates`;
}

// Filter blocks that belong to this client's projects
const clientProjectIds = projects.map(p => p.pageId);
const clientBlocks = notionBlocks.filter(block => {
  if (!block.parent?.page_id) return false;
  const blockPageId = block.parent.page_id.replace(/-/g, '');
  return clientProjectIds.some(projectId => projectId === blockPageId);
});

console.log('Block filtering:', {
  totalBlocks: notionBlocks.length,
  clientProjectIds: clientProjectIds.length,
  filteredBlocks: clientBlocks.length
});

// Filter Slack messages from last 48 hours
const now = new Date();
const fortyEightHoursAgo = new Date(now.getTime() - (48 * 60 * 60 * 1000));

const recentMessages = slackHistory
  .filter(msg => {
    if (!msg || !msg.ts) return false;
    const msgDate = new Date(msg.ts * 1000);
    const isRecent = msgDate >= fortyEightHoursAgo;
    const hasContent = msg.text || msg.subtype;
    const notBot = !msg.bot_id;
    return isRecent && hasContent && notBot;
  })
  .slice(0, 20)
  .map(msg => ({
    text: msg.text || `${msg.subtype}: ${msg.user}`,
    user: msg.user,
    timestamp: new Date(msg.ts * 1000).toISOString(),
    thread_ts: msg.thread_ts,
    subtype: msg.subtype
  }));

console.log('Slack message filtering:', {
  totalSlackMessages: slackHistory.length,
  filteredRecentMessages: recentMessages.length,
  fortyEightHoursAgo: fortyEightHoursAgo.toISOString()
});

// Build final result
const result = {
  currentClient: currentClient,
  targetChannel: targetChannel,
  projects: projects,
  blocks: clientBlocks,
  slackContext: {
    channelName: targetChannel,
    recentMessages: recentMessages,
    messageCount: recentMessages.length,
    timeWindow: '48 hours',
    totalSlackItems: slackHistory.length,
    filteredCount: recentMessages.length
  },
  metadata: {
    timestamp: new Date().toISOString(),
    processedClient: currentClient,
    projectCount: projects.length,
    blockCount: clientBlocks.length,
    hasSlackContext: recentMessages.length > 0,
    inputsProcessed: allInputs.length,
    totalNotionBlocks: notionBlocks.length
  }
};

console.log('Final result summary:', {
  client: result.currentClient,
  projects: result.projects.length,
  blocks: result.blocks.length,
  slackMessages: result.slackContext.messageCount,
  totalProcessed: allInputs.length
});

return [{ json: result }];

