// Normalize input from either form or webhook
const inputData = items[0].json;
let normalizedData = {};

// Map form fields to standard names
if (inputData['Company Name']) {
  // Form input
  normalizedData = {
    company_name: inputData['Company Name'],
    website_url: inputData['Website URL'],
    industry: inputData['Industry'],
    target_locations: inputData['Target Locations'],
    competitors: inputData['Competitors (comma-separated)'] || '',
    business_goals: inputData['Business Goals'],
    target_audience: inputData['Target Audience'],
    contact_email: inputData['Contact Email'],
    package_preference: inputData['Package Preference'] ? inputData['Package Preference'].toLowerCase().split(' ')[0] : 'growth',
    urgency: inputData['Timeline Urgency'] ? inputData['Timeline Urgency'].toLowerCase().split(' ')[0] : 'standard',
    additional_notes: inputData['Additional Notes'] || ''
  };
} else {
  // Webhook input (already normalized)
  normalizedData = inputData;
}

return [{ json: normalizedData }];

// Validate required fields
const requiredFields = ['company_name', 'website_url', 'industry', 'target_locations', 'business_goals', 'target_audience', 'contact_email'];
const errors = [];
const inputData = items[0].json;

// Check required fields
requiredFields.forEach(field => {
  if (!inputData[field] || inputData[field].trim() === '') {
    errors.push(`Missing required field: ${field}`);
  }
});

// URL validation
if (inputData.website_url) {
  const urlPattern = /^https?:\/\/.+\..+/;
  if (!urlPattern.test(inputData.website_url)) {
    errors.push('Invalid website URL format');
  }
}

// Email validation
if (inputData.contact_email) {
  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailPattern.test(inputData.contact_email)) {
    errors.push('Invalid email format');
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed: ${errors.join(', ')}`);
}

// Normalize data
const normalizedData = {
  ...inputData,
  competitors_array: inputData.competitors 
    ? inputData.competitors.split(',').map(c => c.trim()).filter(c => c)
    : ['Competitor 1', 'Competitor 2', 'Competitor 3'],
  formatted_date: new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  }),
  package_preference: inputData.package_preference || 'growth',
  urgency: inputData.urgency || 'standard',
  agency_name: 'Butterfly Digital'
};

return [{ json: normalizedData }];

