// Compile all AI outputs into a formatted MARKDOWN document
const sections = [];

// Get all node outputs
const nodes = [
  { name: 'AI: Title Slide', title: 'Title', level: 1 },
  { name: 'AI: Value of SEO', title: 'The Value of SEO', level: 2 },
  { name: 'AI: Current State', title: 'Current State Analysis', level: 2 },
  { name: 'AI: Key Issues', title: 'Key Issues to Address', level: 2 },
  { name: 'AI: Performance Marketing', title: 'SEO as Performance Marketing', level: 2 },
  { name: 'AI: Focus Areas Overview', title: 'Strategic Focus Areas', level: 2 },
  { name: 'AI: Website Optimization', title: 'Website Optimization', level: 3 },
  { name: 'AI: Local SEO', title: 'Local SEO & Google Business Profile', level: 3 },
  { name: 'AI: Content Strategy', title: 'Content Strategy', level: 3 },
  { name: 'AI: Backlink Strategy', title: 'Backlink Strategy', level: 3 },
  { name: 'AI: Social Media SEO', title: 'Social Media SEO', level: 3 },
  { name: 'AI: Technical SEO', title: 'Technical SEO', level: 3 },
  { name: 'AI: SEO Packages', title: 'SEO Investment Options', level: 2 },
  { name: 'AI: ROI Projections', title: 'Expected Results & ROI', level: 2 },
  { name: 'AI: Conclusion', title: 'Strategic Conclusion', level: 2 },
  { name: 'AI: Call to Action', title: 'Next Steps', level: 2 }
];

// Add header with metadata
sections.push(`# SEO Strategy Proposal\n`);
sections.push(`**Client:** ${$('Set Global Variables').item.json.company_name}\n`);
sections.push(`**Prepared by:** ${$('Set Global Variables').item.json.agency_name}\n`);
sections.push(`**Date:** ${$('Set Global Variables').item.json.date}\n`);
sections.push(`\n---\n`);

// Add table of contents
sections.push(`## Table of Contents\n`);
const tocEntries = nodes.filter(n => n.level <= 2).map((node, index) => {
  const indent = '  '.repeat(node.level - 1);
  return `${indent}${index + 1}. [${node.title}](#${node.title.toLowerCase().replace(/ /g, '-')})`;
});
sections.push(tocEntries.join('\n'));
sections.push(`\n---\n`);

// Add each section with proper markdown formatting
nodes.forEach(node => {
  try {
    const content = $(node.name).item.json.output || $(node.name).item.json.text || 'Content pending...';
    const heading = '#'.repeat(node.level);

    sections.push(`\n${heading} ${node.title}\n`);

    // Format content for markdown
    const formattedContent = content
      .replace(/ðŸŸ¥/g, '**[HIGH PRIORITY]**')
      .replace(/ðŸŸ¦/g, '**[IMPORTANT]**')
      .trim();

    sections.push(formattedContent);
    sections.push('\n');
  } catch (error) {
    sections.push(`\n${'#'.repeat(node.level)} ${node.title}\n`);
    sections.push('*[Section content generation in progress]*\n');
  }
});

// Add footer with metadata
sections.push(`\n---\n`);
sections.push(`### Proposal Metadata\n`);
sections.push(`- **Generated:** ${new Date().toISOString()}`);
sections.push(`- **Client:** ${$('Set Global Variables').item.json.company_name}`);
sections.push(`- **Industry:** ${$('Set Global Variables').item.json.industry}`);
sections.push(`- **Location:** ${$('Set Global Variables').item.json.location}`);
sections.push(`- **Package Preference:** ${$('Set Global Variables').item.json.package_preference}`);
sections.push(`\n---\n`);
sections.push(`\n*This proposal was generated using AI-powered analysis tailored to your specific business needs.*`);

// Combine into single markdown document
const markdownDocument = sections.join('\n');

return [{
  json: {
    markdown_proposal: markdownDocument,
    client_email: $('Set Global Variables').item.json.contact_email,
    company_name: $('Set Global Variables').item.json.company_name,
    original_input: $('Data Validation').item.json,
    generation_time: new Date().toISOString()
  }
}];

// Prepare webhook response with markdown content
const response = {
  success: true,
  message: 'SEO proposal generated successfully',
  company: items[0].json.company_name,
  generatedAt: items[0].json.generation_time,
  proposal: {
    markdown: items[0].json.markdown_proposal,
    metadata: {
      client: items[0].json.company_name,
      email: items[0].json.client_email,
      industry: items[0].json.original_input.industry,
      location: items[0].json.original_input.target_locations
    }
  }
};

return [{ json: response }];

