// Merge inputs from either form or webhook trigger
const input = $input.first().json;

// Normalize field names (form uses underscores, webhook might use different naming)
const normalizedData = {
  // Contact Information
  email: input['Contact Email'] || input.email || input.contact_email || '',
  firstName: input['First Name'] || input.firstName || input.first_name || '',
  lastName: input['Last Name'] || input.lastName || input.last_name || '',

  // Business Information
  businessName: input['Business Name'] || input.businessName || input.business_name || '',
  businessDescription: input['Business Description'] || input.businessDescription || input.description || '',
  websiteUrl: input['Website URL'] || input.websiteUrl || input.website_url || '',

  // Competition & Market
  competitors: input['Top Competitors'] || input.competitors || input.top_competitors || '',

  // Goals & Strategy
  businessGoals: input['Business Growth Goals'] || input.businessGoals || input.business_growth_goals || '',
  serviceType: input['Service Type Needed'] || input.serviceType || input.service_type || 'Comprehensive Digital Marketing',

  // Targeting
  targetLocations: input['Target Locations'] || input.targetLocations || input.target_locations || '',
  targetAudience: input['Target Audience/Personas'] || input.targetAudience || input.target_personas || '',

  // Budget & Timeline
  monthlyBudget: input['Monthly Budget'] || input.monthlyBudget || input.monthly_budget || input.monthly_ad_budget || '',
  timeline: input['Timeline/Urgency'] || input.timeline || input.urgency || '1-2 months',

  // Campaign Details
  campaignTypes: input['Campaign Types'] || input.campaignTypes || input.campaign_types || [],

  // Additional Information
  currentChallenges: input['Current Marketing Challenges'] || input.currentChallenges || input.current_challenges || '',
  additionalInfo: input['Additional Information'] || input.additionalInfo || input.additional_information || '',

  // Extended fields from webhook (optional)
  googleAdsCID: input.google_ads_cid || input['Google Ads CID'] || '',
  advertisingPurpose: input.advertising_purpose || input['Advertising Purpose'] || '',
  targetKeywords: input.target_keywords || input['Target Keywords'] || '',
  landingPages: input.landing_pages || input['Landing Pages'] || '',
  currency: input.currency || input['Currency'] || 'USD',
  paymentMethod: input.payment_method || input['Payment Method'] || '',

  // Tracking
  formPurpose: input.form_purpose || input['Form Purpose'] || 'Proposal Request',
  submissionDate: new Date().toISOString(),

  // Scraping configuration
  maxPages: input.maxPages || 15,
  useApify: input.useApify || false
};

// Process competitors into array if string
if (typeof normalizedData.competitors === 'string') {
  normalizedData.competitorsList = normalizedData.competitors
    .split(/[\n,;]/)
    .map(c => c.trim())
    .filter(c => c.length > 0);
} else {
  normalizedData.competitorsList = [];
}

// Ensure website URL has protocol
if (normalizedData.websiteUrl && !normalizedData.websiteUrl.startsWith('http')) {
  normalizedData.websiteUrl = 'https://' + normalizedData.websiteUrl;
}

// Validate required fields
const requiredFields = ['email', 'businessName', 'websiteUrl', 'businessGoals'];
const missingFields = requiredFields.filter(field => !normalizedData[field]);

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

return {
  json: normalizedData
};

// Enhanced configuration for web scraping with client data
const clientData = $input.first().json;
const websiteUrl = clientData.websiteUrl;
const competitors = clientData.competitorsList || [];
const maxPages = Math.min(clientData.maxPages || 15, 15);
const useApify = clientData.useApify || false;

// Build list of URLs to analyze (client site + competitors)
const urlsToAnalyze = [websiteUrl];
if (competitors.length > 0) {
  // Add up to 3 competitor sites
  competitors.slice(0, 3).forEach(comp => {
    if (comp && comp.includes('.')) {
      if (!comp.startsWith('http')) {
        comp = 'https://' + comp;
      }
      urlsToAnalyze.push(comp);
    }
  });
}

// Initialize scraping configuration
const scrapingConfig = {
  clientData: clientData,
  urlsToAnalyze: urlsToAnalyze,
  baseUrl: websiteUrl,
  maxPages: maxPages,
  useApify: useApify,
  visitedUrls: new Set(),
  pagesToScrape: urlsToAnalyze,
  scrapedContent: []
};

// If using Apify, prepare the configuration
if (useApify) {
  return {
    json: {
      ...clientData,
      useApify: true,
      apifyConfig: {
        startUrls: urlsToAnalyze.map(url => ({ url })),
        maxRequestsPerCrawl: maxPages,
        linkSelector: 'a[href]',
        pseudoUrls: urlsToAnalyze.map(url => ({
          purl: url.replace(/https?:\/\//, '') + '/**',
          method: 'GET'
        })),
        keepUrlFragments: false,
        proxyConfiguration: {
          useApifyProxy: true
        }
      },
      websiteUrl: websiteUrl,
      urlsToAnalyze: urlsToAnalyze
    }
  };
} else {
  // For native scraping, return URLs to process
  return {
    json: {
      ...clientData,
      useApify: false,
      baseUrl: websiteUrl,
      maxPages: maxPages,
      currentPage: 1,
      urlsToScrape: urlsToAnalyze,
      urlsToAnalyze: urlsToAnalyze
    }
  };
}

// Process scraped content and prepare for next iteration
const items = $input.all();
const allContent = [];
const allLinks = new Set();
const clientData = items[0].json;
const baseUrl = clientData.baseUrl || '';
const maxPages = clientData.maxPages || 15;
let currentPage = clientData.currentPage || 1;

// Collect content from all items
items.forEach(item => {
  const content = {
    url: item.json.url || baseUrl,
    title: item.json.title || '',
    headings: item.json.headings || [],
    paragraphs: item.json.paragraphs || [],
    metaDescription: item.json.metaDescription || ''
  };

  allContent.push(content);

  // Collect links for crawling
  if (item.json.links && Array.isArray(item.json.links)) {
    item.json.links.forEach(link => {
      // Filter and normalize links
      if (link && link.startsWith('/')) {
        link = new URL(link, baseUrl).href;
      }
      if (link && link.startsWith(baseUrl)) {
        allLinks.add(link);
      }
    });
  }
});

// Prepare next batch of URLs to scrape
const urlsToScrape = Array.from(allLinks).slice(0, maxPages - currentPage);

return {
  json: {
    ...clientData,
    scrapedContent: allContent,
    totalPagesScraped: currentPage,
    remainingPages: maxPages - currentPage,
    nextUrlsToScrape: urlsToScrape,
    allContentCollected: allContent
  }
};

// Combine all scraped content with client data for AI processing
const data = $input.first().json;
const allContent = data.allContentCollected || [];
const clientData = data;

// Create a comprehensive content summary with client context
let contentSummary = {
  // Client Information
  clientName: `${clientData.firstName} ${clientData.lastName}`,
  clientEmail: clientData.email,
  businessName: clientData.businessName,
  businessDescription: clientData.businessDescription,

  // Business Context
  websiteUrl: clientData.websiteUrl,
  competitors: clientData.competitorsList || [],
  businessGoals: clientData.businessGoals,
  serviceType: clientData.serviceType,

  // Targeting
  targetLocations: clientData.targetLocations,
  targetAudience: clientData.targetAudience,

  // Budget & Timeline
  monthlyBudget: clientData.monthlyBudget,
  timeline: clientData.timeline,

  // Campaign Details
  campaignTypes: clientData.campaignTypes || [],
  currentChallenges: clientData.currentChallenges,
  additionalInfo: clientData.additionalInfo,

  // Scraped Content
  totalPagesAnalyzed: allContent.length,
  compiledContent: ''
};

// Add client-provided context to the compiled content
contentSummary.compiledContent = `
=== CLIENT INFORMATION ===
Business: ${clientData.businessName}
Description: ${clientData.businessDescription}
Goals: ${clientData.businessGoals}
Target Locations: ${clientData.targetLocations}
Target Audience: ${clientData.targetAudience}
Budget Range: ${clientData.monthlyBudget}
Timeline: ${clientData.timeline}
Current Challenges: ${clientData.currentChallenges || 'Not specified'}

=== WEBSITE ANALYSIS ===
`;

// Compile scraped content into a structured format
allContent.forEach((page, index) => {
  const isCompetitor = page.url !== clientData.websiteUrl;
  const siteLabel = isCompetitor ? '[COMPETITOR] ' : '[CLIENT SITE] ';

  contentSummary.compiledContent += `\n\n--- ${siteLabel}Page ${index + 1}: ${page.url} ---\n`;
  contentSummary.compiledContent += `Title: ${page.title}\n`;

  if (page.metaDescription) {
    contentSummary.compiledContent += `Description: ${page.metaDescription}\n`;
  }

  if (page.headings && page.headings.length > 0) {
    contentSummary.compiledContent += `\nKey Topics:\n`;
    page.headings.slice(0, 10).forEach(heading => {
      contentSummary.compiledContent += `- ${heading}\n`;
    });
  }

  if (page.paragraphs && page.paragraphs.length > 0) {
    contentSummary.compiledContent += `\nContent Summary:\n`;
    const relevantParagraphs = page.paragraphs
      .filter(p => p && p.length > 50)
      .slice(0, 5);
    relevantParagraphs.forEach(para => {
      contentSummary.compiledContent += `${para.substring(0, 200)}...\n`;
    });
  }
});

return {
  json: contentSummary
};

