finalProposal += `- Your stated business goals and growth objectives\n`;
finalProposal += `- Market analysis of your industry and competitors\n`;
finalProposal += `- Your budget and timeline requirements\n\n`;
finalProposal += `*This proposal was generated using AI-powered analysis of your website content and business requirements. `;
finalProposal += `All recommendations are tailored specifically to ${businessName}'s needs.*\n\n`;
finalProposal += `**Validity:** This proposal is valid for 30 days from the date of generation.\n\n`;
finalProposal += `**Confidentiality:** This proposal contains confidential information and is intended solely for ${clientName} at ${businessName}.\n\n`;
finalProposal += `**Next Step:** Please contact us to schedule a discovery call to discuss this proposal in detail.\n`;

return {
  json: {
    proposal: finalProposal,
    filename: `proposal_${businessName.replace(/[^a-z0-9]/gi, '_')}_${serviceType.replace(/[^a-z0-9]/gi, '_')}_${Date.now()}.md`,
    businessName: businessName,
    clientEmail: clientEmail,
    websiteUrl: websiteUrl,
    timestamp: timestamp
  }
};
