// Get form data
const formData = $input.first().json;

// Map presentation type to appropriate settings
const presentationTypeMap = {
  'Analysis Presentation': {
    tone: 'analytical, objective, data-driven',
    instructions: 'Focus on data insights, trends, and key findings. Include clear visualizations and metrics.',
    textMode: 'generate'
  },
  'Proposal Presentation': {
    tone: 'persuasive, professional, solution-oriented',
    instructions: 'Structure as problem-solution-benefits. Include clear value propositions and call to action.',
    textMode: 'generate'
  },
  'Summary Presentation': {
    tone: 'concise, clear, informative',
    instructions: 'Highlight key points and main takeaways. Keep content brief and impactful.',
    textMode: 'condense'
  },
  'Educational Presentation': {
    tone: 'instructional, clear, engaging',
    instructions: 'Structure content for learning. Include examples and explanations.',
    textMode: 'generate'
  },
  'Business Report': {
    tone: 'professional, detailed, factual',
    instructions: 'Include executive summary, detailed findings, and recommendations.',
    textMode: 'generate'
  },
  'Project Update': {
    tone: 'informative, progress-focused',
    instructions: 'Highlight milestones, current status, next steps, and any blockers.',
    textMode: 'generate'
  },
  'Sales Pitch': {
    tone: 'persuasive, enthusiastic, benefit-focused',
    instructions: 'Focus on customer benefits, ROI, and competitive advantages.',
    textMode: 'generate'
  },
  'Training Material': {
    tone: 'educational, step-by-step, practical',
    instructions: 'Include clear instructions, examples, and practice exercises.',
    textMode: 'generate'
  }
};

// Get presentation type settings
const typeSettings = presentationTypeMap[formData['Presentation Type']] || presentationTypeMap['Summary Presentation'];

// Build additional instructions
let additionalInstructions = typeSettings.instructions;
if (formData['Presentation Instructions']) {
  additionalInstructions = formData['Presentation Instructions'] + '. ' + additionalInstructions;
}

// Map theme names - all valid Gamma themes
const themeMap = {
  'Pearl': 'Pearl',
  'Vortex': 'Vortex',
  'Chisel': 'Chisel',
  'Stardust': 'Stardust',
  'Seafoam': 'Seafoam',
  'Nebulae': 'Nebulae',
  'Creme': 'Creme',
  'Lux': 'Lux',
  'Consultant': 'Consultant',
  'Marine': 'Marine',
  'Elysia': 'Elysia',
  'Prism': 'Prism',
  'Lunaria': 'Lunaria',
  'Night Sky': 'Night Sky',
  'Commons': 'Commons',
  'Gamma': 'Gamma',
  'Gamma Dark': 'Gamma Dark',
  'Dialogue': 'Dialogue',
  'Founder': 'Founder',
  'Lavender': 'Lavender',
  'Indigo': 'Indigo',
  'Howlite': 'Howlite',
  'Onyx': 'Onyx',
  'Atmosphere': 'Atmosphere',
  'Blueberry': 'Blueberry',
  'Kraft': 'Kraft',
  'Mystique': 'Mystique',
  'Petrol': 'Petrol',
  'Blues': 'Blues',
  'Peach': 'Peach',
  'Incandescent': 'Incandescent',
  'Oatmeal': 'Oatmeal',
  'Sanguine': 'Sanguine',
  'Sage': 'Sage',
  'Verdigris': 'Verdigris',
  'Ash': 'Ash',
  'Coal': 'Coal',
  'Flamingo': 'Flamingo',
  'Canaveral': 'Canaveral',
  'Oasis': 'Oasis',
  'Fluo': 'Fluo',
  'Finesse': 'Finesse',
  'Electric': 'Electric',
  'Zephyr': 'Zephyr',
  'Chimney Smoke': 'Chimney Smoke',
  'Chimney Dust': 'Chimney Dust',
  'Icebreaker': 'Icebreaker',
  'Blue Steel': 'Blue Steel',
  'Daydream': 'Daydream',
  'Orbit': 'Orbit'
};

// Map image sources
const imageSourceMap = {
  'AI Generated Images': 'aiGenerated',
  'Stock Photos (Unsplash)': 'unsplash',
  'Icons (Pictographic)': 'pictographic',
  'GIFs (Giphy)': 'giphy',
  'Web Images - All': 'webAllImages',
  'Web Images - Free to Use': 'webFreeToUse',
  'Web Images - Free Commercial Use': 'webFreeToUseCommercially',
  'No Images': 'noImages',
  'Placeholders Only': 'placeholder'
};

// Map AI image models
const aiModelMap = {
  'Auto-Select (Let Gamma Choose)': null,
  'Dall E 3': 'dall-e-3',
  'Flux Fast 1.1': 'flux-1-quick',
  'Flux Pro': 'flux-1-pro',
  'Flux Ultra': 'flux-1-ultra',
  'Flux Kontext Fast': 'flux-kontext-fast',
  'Flux Kontext Pro': 'flux-kontext-pro',
  'Flux Kontext Max': 'flux-kontext-max',
  'GPT Image': 'gpt-image-1-medium',
  'Ideogram 3': 'ideogram-v3',
  'Ideogram 3 Turbo': 'ideogram-v3-turbo',
  'Imagen 3': 'imagen-3-pro',
  'Imagen 3 Fast': 'imagen-3-flash',
  'Imagen 4': 'imagen-4-pro',
  'Imagen 4 Ultra': 'imagen-4-ultra',
  'Leonardo Phoenix': 'leonardo-phoenix',
  'Luma Photon': 'luma-photon-1',
  'Luma Photon Flash': 'luma-photon-flash-1',
  'Recraft': 'recraft-v3',
  'Recraft Vector Illustration': 'recraft-v3-svg'
};

// Map content detail levels
const detailMap = {
  'Brief': 'brief',
  'Medium': 'medium',
  'Detailed': 'detailed',
  'Extensive': 'extensive'
};

// Map export formats
const exportMap = {
  'PDF': 'pdf',
  'PowerPoint (PPTX)': 'pptx',
  'None (Gamma Link Only)': null
};

// Build tone
let tone = formData['Tone'] ? formData['Tone'].toLowerCase() : '';
if (typeSettings.tone) {
  tone = tone ? `${tone}, ${typeSettings.tone}` : typeSettings.tone;
}

// Build the Gamma API request
const gammaRequest = {
  inputText: formData['Transcript'],
  textMode: typeSettings.textMode,
  format: 'presentation',
  additionalInstructions: additionalInstructions.substring(0, 500) // Character limit
};

// Add optional parameters
if (formData['Number of Slides']) {
  gammaRequest.numCards = Math.min(Math.max(parseInt(formData['Number of Slides']), 1), 60);
}

if (formData['Theme']) {
  gammaRequest.themeName = themeMap[formData['Theme']] || 'Professional';
}

if (exportMap[formData['Export Format']]) {
  gammaRequest.exportAs = exportMap[formData['Export Format']];
}

// Add text options
gammaRequest.textOptions = {
  amount: detailMap[formData['Content Detail Level']] || 'medium',
  language: 'en'
};

if (tone) {
  gammaRequest.textOptions.tone = tone;
}

if (formData['Target Audience']) {
  gammaRequest.textOptions.audience = formData['Target Audience'];
}

// Add image options
const imageSource = imageSourceMap[formData['Image Source']] || 'aiGenerated';
gammaRequest.imageOptions = {
  source: imageSource
};

if (imageSource === 'aiGenerated') {
  // Set the AI model if specified
  const selectedModel = aiModelMap[formData['AI Image Model']];
  if (selectedModel) {
    gammaRequest.imageOptions.model = selectedModel;
  }
  // If 'Auto-Select' or not specified, let Gamma choose

  // Set the image style
  if (formData['AI Image Style']) {
    gammaRequest.imageOptions.style = formData['AI Image Style'];
  } else {
    gammaRequest.imageOptions.style = 'photorealistic, professional';
  }
}

// Add card options
gammaRequest.cardOptions = {
  dimensions: 'fluid'
};

// Add sharing options
gammaRequest.sharingOptions = {
  workspaceAccess: 'view',
  externalAccess: 'noAccess'
};

return [
  {
    json: {
      gammaRequest: gammaRequest,
      originalFormData: formData
    }
  }
];

const items = $input.all();
const response = items[0].json;

// Check if the API call was successful (201 = Created, 200 = OK)
if (response.statusCode && response.statusCode !== 200 && response.statusCode !== 201) {
  // Handle error
  const errorMessage = response.body?.error || response.body?.message || 'Failed to generate presentation';

  return [
    {
      json: {
        success: false,
        error: errorMessage,
        statusCode: response.statusCode,
        originalRequest: items[0].json.originalFormData
      }
    }
  ];
}

// Extract the presentation details from the response
const presentationData = response.body || response;

// Build success response
const result = {
  success: true,
  presentationId: presentationData.generationId || presentationData.id,
  presentationUrl: presentationData.url || presentationData.viewUrl || `https://gamma.app/docs/${presentationData.generationId || presentationData.id}`,
  editUrl: presentationData.editUrl,
  status: presentationData.status || 'processing',
  createdAt: presentationData.createdAt || new Date().toISOString(),
  originalRequest: items[0].json.originalFormData
};

// Add message about generation status
if (presentationData.generationId) {
  result.message = 'Presentation is being generated. The URL will be active once processing is complete.';
}

// Add export URLs if available
if (presentationData.exportUrl) {
  result.exportUrl = presentationData.exportUrl;
}

if (presentationData.pdfUrl) {
  result.pdfUrl = presentationData.pdfUrl;
}

if (presentationData.pptxUrl) {
  result.pptxUrl = presentationData.pptxUrl;
}

// Add any additional metadata
if (presentationData.metadata) {
  result.metadata = presentationData.metadata;
}

return [
  {
    json: result
  }
];

// Process AI output and prepare Gamma API request
const input = $input.first()?.json || {};
const presentation = input.output?.presentation || input.presentation || {};

if (!presentation.inputText) {
  throw new Error('No presentation data extracted from transcript');
}

// Map presentation types to Gamma settings
const typeSettings = {
  'analysis': {
    tone: 'analytical, objective, data-driven',
    instructions: 'Focus on data insights, trends, and key findings. Include clear visualizations and metrics.'
  },
  'proposal': {
    tone: 'persuasive, professional, solution-oriented',
    instructions: 'Structure as problem-solution-benefits. Include clear value propositions and call to action.'
  },
  'summary': {
    tone: 'concise, clear, informative',
    instructions: 'Highlight key points and main takeaways. Keep content brief and impactful.'
  },
  'educational': {
    tone: 'instructional, clear, engaging',
    instructions: 'Structure content for learning. Include examples and explanations.'
  },
  'report': {
    tone: 'professional, detailed, factual',
    instructions: 'Include executive summary, detailed findings, and recommendations.'
  },
  'update': {
    tone: 'informative, progress-focused',
    instructions: 'Highlight milestones, current status, next steps, and any blockers.'
  },
  'pitch': {
    tone: 'persuasive, enthusiastic, benefit-focused',
    instructions: 'Focus on customer benefits, ROI, and competitive advantages.'
  },
  'training': {
    tone: 'educational, step-by-step, practical',
    instructions: 'Include clear instructions, examples, and practice exercises.'
  }
};

// Map AI image models to Gamma API format
const imageModelMap = {
  'dall-e-3': 'dall-e-3',
  'flux-1-pro': 'flux-1-pro',
  'imagen-4-pro': 'imagen-4-pro',
  'leonardo-phoenix': 'leonardo-phoenix',
  'recraft-v3': 'recraft-v3'
};

// Get type-specific settings
const typeConfig = typeSettings[presentation.presentationType] || typeSettings['summary'];

// Combine instructions
let instructions = presentation.additionalInstructions || '';
if (typeConfig.instructions) {
  instructions = instructions ? 
    `${instructions}. ${typeConfig.instructions}` : 
    typeConfig.instructions;
}
instructions = instructions.substring(0, 500);

// Combine tone
let tone = presentation.tone || '';
if (typeConfig.tone) {
  tone = tone ? `${tone}, ${typeConfig.tone}` : typeConfig.tone;
}

// Build Gamma API request
const gammaRequest = {
  inputText: presentation.inputText,
  textMode: presentation.textMode || 'generate',
  format: 'presentation',
  themeName: presentation.theme || 'Consultant',
  numCards: presentation.numCards || 10,
  additionalInstructions: instructions
};

// Add text options
gammaRequest.textOptions = {
  amount: presentation.detailLevel || 'medium',
  language: 'en'
};

if (tone) {
  gammaRequest.textOptions.tone = tone;
}

if (presentation.audience) {
  gammaRequest.textOptions.audience = presentation.audience;
}

// Add image options
gammaRequest.imageOptions = {
  source: 'aiGenerated'
};

if (presentation.imageModel && imageModelMap[presentation.imageModel]) {
  gammaRequest.imageOptions.model = imageModelMap[presentation.imageModel];
}

if (presentation.imageStyle) {
  gammaRequest.imageOptions.style = presentation.imageStyle;
}

// Add export format if specified
if (presentation.exportFormat && presentation.exportFormat !== 'none') {
  gammaRequest.exportAs = presentation.exportFormat;
}

// Add card options
gammaRequest.cardOptions = {
  dimensions: 'fluid'
};

// Add sharing options
gammaRequest.sharingOptions = {
  workspaceAccess: 'view',
  externalAccess: 'noAccess'
};

return [{
  json: {
    gammaRequest: gammaRequest,
    metadata: {
      title: presentation.title,
      presentationType: presentation.presentationType,
      extractedTopics: presentation.metadata?.extractedTopics || [],
      estimatedDuration: presentation.metadata?.estimatedDuration || 'Unknown',
      complexity: presentation.metadata?.complexity || 'intermediate',
      processedAt: new Date().toISOString()
    }
  }
}];

// Process Gamma API response
const response = $input.first()?.json || {};
const metadata = $input.all()[0]?.json?.metadata || {};

// Check response status
if (response.statusCode !== 200 && response.statusCode !== 201) {
  return [{
    json: {
      success: false,
      error: response.body?.error || response.body?.message || 'Failed to generate presentation',
      statusCode: response.statusCode,
      metadata: metadata
    }
  }];
}

// Extract presentation data
const presentationData = response.body || response;

// Build success response
const result = {
  success: true,
  presentationId: presentationData.generationId || presentationData.id,
  presentationUrl: presentationData.url || `https://gamma.app/docs/${presentationData.generationId || presentationData.id}`,
  editUrl: presentationData.editUrl,
  status: presentationData.status || 'processing',
  createdAt: presentationData.createdAt || new Date().toISOString(),
  metadata: metadata
};

// Add export URLs if available
if (presentationData.pdfUrl) {
  result.pdfUrl = presentationData.pdfUrl;
}

if (presentationData.pptxUrl) {
  result.pptxUrl = presentationData.pptxUrl;
}

// Add processing message
if (presentationData.generationId) {
  result.message = 'Presentation is being generated. The URL will be active once processing is complete.';
}

return [{
  json: result
}];
